export class ServersService {
    constructor() {
        this.servers = [
            {
                id: 1,
                name: 'Productionserver',
                status: 'online'
            },
            {
                id: 2,
                name: 'Testserver',
                status: 'offline'
            },
            {
                id: 3,
                name: 'Devserver',
                status: 'offline'
            }
        ];
    }
    getServers() {
        return this.servers;
    }
    getServer(id) {
        const server = this.servers.find((s) => {
            return s.id === id;
        });
        return server;
    }
    updateServer(id, serverInfo) {
        const server = this.servers.find((s) => {
            return s.id === id;
        });
        if (server) {
            server.name = serverInfo.name;
            server.status = serverInfo.status;
        }
    }
}
